(138, 'biblio', 'O:8:"stdClass":83:{s:3:"nid";s:3:"138";s:3:"vid";s:3:"138";s:4:"type";s:6:"biblio";s:6:"status";s:1:"1";s:7:"created";s:10:"1218729758";s:7:"changed";s:10:"1241526436";s:7:"comment";s:1:"0";s:7:"promote";s:1:"0";s:6:"sticky";s:1:"0";s:18:"revision_timestamp";s:10:"1241526436";s:5:"title";s:65:"Choosing an Identifying Set of Matching or Conditioning Variables";s:4:"body";s:0:"";s:6:"teaser";s:0:"";s:3:"log";s:0:"";s:6:"format";s:1:"1";s:3:"uid";s:1:"4";s:4:"name";s:6:"aglynn";s:7:"picture";s:0:"";s:4:"data";s:24:"a:1:{s:7:"contact";i:1;}";s:11:"biblio_type";s:3:"121";s:13:"biblio_number";s:0:"";s:14:"biblio_section";s:0:"";s:19:"biblio_other_number";s:0:"";s:22:"biblio_secondary_title";s:0:"";s:21:"biblio_tertiary_title";s:0:"";s:18:"biblio_short_title";s:0:"";s:22:"biblio_alternate_title";s:0:"";s:23:"biblio_translated_title";s:0:"";s:14:"biblio_authors";s:22:"Adam Glynn;Kevin Quinn";s:24:"biblio_secondary_authors";s:0:"";s:23:"biblio_tertiary_authors";s:0:"";s:18:"biblio_corp_author";s:0:"";s:32:"biblio_other_author_affiliations";s:0:"";s:14:"biblio_edition";s:0:"";s:16:"biblio_publisher";s:0:"";s:27:"biblio_original_publication";s:0:"";s:22:"biblio_reprint_edition";s:0:"";s:22:"biblio_place_published";s:0:"";s:11:"biblio_year";s:4:"2008";s:13:"biblio_volume";s:0:"";s:24:"biblio_number_of_volumes";s:0:"";s:12:"biblio_pages";s:0:"";s:11:"biblio_date";s:0:"";s:11:"biblio_isbn";s:0:"";s:11:"biblio_issn";s:0:"";s:11:"biblio_lang";s:0:"";s:13:"biblio_abst_e";s:828:"Political scientists estimate average causal effects with regression or matching techniques, but both techniques require the user to choose a set of matching or conditioning variables. In this paper, we show that the standard advice from both frameworks on how to choose an identifying set of variables is often insufficient and at times misleading. Furthermore, we argue that nonparametric structural equation models (NPSEMs) solve this problem by creating a framework that provides simple rules for choosing an identifying set and that this framework is consistent with both the standard regression and Neyman-Rubin models. Finally, we demonstrate that NPSEMs allow the specification of causal modeling assumptions in a manner that is compatible with the mechanistic view of causation commonly invoked by political scientists.";s:13:"biblio_abst_f";s:0:"";s:16:"biblio_full_text";s:1:"0";s:15:"biblio_keywords";s:0:"";s:10:"biblio_url";s:0:"";s:10:"biblio_doi";s:0:"";s:12:"biblio_issue";s:0:"";s:19:"biblio_type_of_work";s:0:"";s:23:"biblio_accession_number";s:0:"";s:18:"biblio_call_number";s:0:"";s:12:"biblio_notes";s:0:"";s:12:"biblio_coins";s:318:"<span class="Z3988" title="ctx_ver=Z39.88-2004&amp;rft_val_fmt=info%3Aofi%2Ffmt%3Akev%3Amtx%3Adc&amp;rfr_id=info%3Asid%2Fscholar.iq.harvard.edu&amp;rft.title=Choosing+an+Identifying+Set+of+Matching+or+Conditioning+Variables&amp;rft.date=2008&amp;rft.aulast=Glynn&amp;rft.aufirst=Adam&amp;rft.au=Quinn%2C+Kevin"></span>";s:21:"biblio_research_notes";s:0:"";s:14:"biblio_custom1";s:0:"";s:14:"biblio_custom2";s:0:"";s:14:"biblio_custom3";s:0:"";s:14:"biblio_custom4";s:0:"";s:14:"biblio_custom5";s:0:"";s:14:"biblio_custom6";s:0:"";s:14:"biblio_custom7";s:0:"";s:19:"biblio_auth_address";s:0:"";s:21:"biblio_remote_db_name";s:0:"";s:25:"biblio_remote_db_provider";s:0:"";s:14:"biblio_citekey";s:3:"138";s:12:"biblio_label";s:0:"";s:18:"biblio_access_date";s:0:"";s:16:"biblio_type_name";s:10:"Manuscript";s:4:"path";s:66:"aglynn/choosing-identifying-set-matching-or-conditioning-variables";s:9:"og_groups";a:1:{i:0;i:31;}s:14:"og_groups_both";a:1:{i:31;s:6:"aglynn";}s:9:"og_public";b:1;s:4:"tags";a:1:{i:6;a:2:{i:66;O:8:"stdClass":5:{s:3:"tid";s:2:"66";s:3:"vid";s:1:"6";s:4:"name";s:27:"Choosing an Identifying Set";s:11:"description";s:0:"";s:6:"weight";s:1:"0";}i:53;O:8:"stdClass":5:{s:3:"tid";s:2:"53";s:3:"vid";s:1:"6";s:4:"name";s:13:"working paper";s:11:"description";s:0:"";s:6:"weight";s:1:"0";}}}s:22:"last_comment_timestamp";s:10: