(463, 'scholar_page', 'O:8:"stdClass":29:{s:3:"nid";s:3:"463";s:3:"vid";s:3:"463";s:4:"type";s:12:"scholar_page";s:6:"status";s:1:"1";s:7:"created";s:10:"1240412573";s:7:"changed";s:10:"1241635640";s:7:"comment";s:1:"0";s:7:"promote";s:1:"0";s:6:"sticky";s:1:"0";s:18:"revision_timestamp";s:10:"1241635640";s:5:"title";s:12:"Introduction";s:4:"body";s:713:"<p class="style1"><br /></p><p class="style1">I am a Professor at Harvard University''s <a href="http://www.gov.harvard.edu/">Department of Government</a>, a division of the <a href="http://www.fas.harvard.edu/home/">Faculty of Arts and Sciences</a>. My teaching and research focus on the politics of international monetary and financial relations. Tel: 617.496.2386&nbsp; Fax: 617.495.8292</p><p class="style1">Office hours: Tues.10:00-11:30am, 2:00-3:30pm. To sign up for office hours, please use <strong><em><strong><a href="http://tinyurl.com/3frmmb">this sign-up tool</a></strong></em></strong></p><p class="style1"><br /><strong><em> </em></strong> </p> <h5 class="style1"><br /></h5><br /><br /><br /><br />";s:6:"teaser";s:393:"<p class="style1"><br /></p><p class="style1">I am a Professor at Harvard University''s <a href="http://www.gov.harvard.edu/">Department of Government</a>, a division of the <a href="http://www.fas.harvard.edu/home/">Faculty of Arts and Sciences</a>. My teaching and research focus on the politics of international monetary and financial relations. Tel: 617.496.2386&nbsp; Fax: 617.495.8292</p>";s:3:"log";s:0:"";s:6:"format";s:1:"1";s:3:"uid";s:2:"24";s:4:"name";s:8:"jfrieden";s:7:"picture";s:0:"";s:4:"data";s:98:"a:3:{s:6:"submit";s:18:"Create new account";s:7:"form_id";s:13:"user_register";s:7:"contact";i:1;}";s:4:"path";s:21:"jfrieden/introduction";s:9:"og_groups";a:1:{i:0;i:327;}s:14:"og_groups_both";a:1:{i:327;s:8:"jfrieden";}s:9:"og_public";b:1;s:4:"tags";N;s:22:"last_comment_timestamp";s:10:"1240412573";s:17:"last_comment_name";N;s:13:"comment_count";s:1:"0";s:8:"taxonomy";a:0:{}s:5:"files"