(675, 'biblio', 'O:8:"stdClass":83:{s:3:"nid";s:3:"675";s:3:"vid";s:3:"675";s:4:"type";s:6:"biblio";s:6:"status";s:1:"1";s:7:"created";s:10:"1252703733";s:7:"changed";s:10:"1252703733";s:7:"comment";s:1:"0";s:7:"promote";s:1:"0";s:6:"sticky";s:1:"0";s:18:"revision_timestamp";s:10:"1252703733";s:5:"title";s:57:"The Product and Difference Fallacies for Indirect Effects";s:4:"body";s:0:"";s:6:"teaser";s:0:"";s:3:"log";s:0:"";s:6:"format";s:1:"1";s:3:"uid";s:1:"4";s:4:"name";s:6:"aglynn";s:7:"picture";s:0:"";s:4:"data";s:24:"a:1:{s:7:"contact";i:1;}";s:11:"biblio_type";s:4:"1000";s:13:"biblio_number";s:0:"";s:14:"biblio_section";s:0:"";s:19:"biblio_other_number";s:0:"";s:22:"biblio_secondary_title";s:0:"";s:21:"biblio_tertiary_title";s:0:"";s:18:"biblio_short_title";s:0:"";s:22:"biblio_alternate_title";s:0:"";s:23:"biblio_translated_title";s:0:"";s:14:"biblio_authors";s:13:"Adam N. Glynn";s:24:"biblio_secondary_authors";s:0:"";s:23:"biblio_tertiary_authors";s:0:"";s:18:"biblio_corp_author";s:0:"";s:32:"biblio_other_author_affiliations";s:0:"";s:14:"biblio_edition";s:0:"";s:16:"biblio_publisher";s:0:"";s:27:"biblio_original_publication";s:0:"";s:22:"biblio_reprint_edition";s:0:"";s:22:"biblio_place_published";s:0:"";s:11:"biblio_year";s:4:"2009";s:13:"biblio_volume";s:0:"";s:24:"biblio_number_of_volumes";s:0:"";s:12:"biblio_pages";s:0:"";s:11:"biblio_date";s:0:"";s:11:"biblio_isbn";s:0:"";s:11:"biblio_issn";s:0:"";s:11:"biblio_lang";s:0:"";s:13:"biblio_abst_e";s:888:"Political scientists often cite the importance of mechanism specific causal knowledge, both for its intrinsic scientific value and as a necessity for informed policy. In this paper, I explain why two common inferential heuristics for mechanism specific (i.e. indirect) effects can provide misleading answers, such as sign reversals and false null results, even when linear regressions provide unbiased estimates of constituent effects. I illustrate these two fallacies with examples from American and comparative politics, demonstrating that the evidence against a direct effect of ethnic diversity on the likelihood of civil war is likely overstated, and the evidence for a positive American voting ``habit'''' effect depends on previously unspecified micro-foundational assumptions. I conclude by suggesting improvements to research design that ameliorate the effects of these fallacies. ";s:13:"biblio_abst_f";s:0:"";s:16:"biblio_full_text";s:1:"0";s:15:"biblio_keywords";s:60:"indirect effect, causal mechanisms, mediation, path analysis";s:10:"biblio_url";s:0:"";s:10:"biblio_doi";s:0:"";s:12:"biblio_issue";s:0:"";s:19:"biblio_type_of_work";s:0:"";s:23:"biblio_accession_number";s:0:"";s:18:"biblio_call_number";s:0:"";s:12:"biblio_notes";s:0:"";s:12:"biblio_coins";s:284:"<span class="Z3988" title="ctx_ver=Z39.88-2004&amp;rft_val_fmt=info%3Aofi%2Ffmt%3Akev%3Amtx%3Adc&amp;rfr_id=info%3Asid%2Fscholar.iq.harvard.edu&amp;rft.title=The+Product+and+Difference+Fallacies+for+Indirect+Effects&amp;rft.date=2009&amp;rft.aulast=Glynn&amp;rft.aufirst=Adam"></span>";s:21:"biblio_research_notes";s:0:"";s:14:"biblio_custom1";s:0:"";s:14:"biblio_custom2";s:0:"";s:14:"biblio_custom3";s:0:"";s:14:"biblio_custom4";s:0:"";s:14:"biblio_custom5";s:0:"";s:14:"biblio_custom6";s:0:"";s:14:"biblio_custom7";s:0:"";s:19:"biblio_auth_address";s:0:"";s:21:"biblio_remote_db_name";s:0:"";s:25:"biblio_remote_db_provider";s:0:"";s:14:"biblio_citekey";s:3:"675";s:12:"biblio_label";s:0:"";s:18:"biblio_access_date";s:0:"";s:16:"biblio_type_name";s:13:"Working Paper";s:4:"path";s:60:"aglynn/the-product-and-difference-fallacies-indirect-effects";s:9:"og_groups";a:1:{i:0;i:31;}s:14:"og_groups_both";a:1:{i:31;s:6:"aglynn";}s:9:"og_public";b:1;s:4:"tags";N;s:22:"last_comment_timestamp";s:10:"1252703733";s:17:"last_comment_name";N;s:13:"comment_count";s:1:"0";s:8:"taxonomy";a:0:{}s:5:"files";a:1:{i:327;O:8:"stdClass":9:{s:3:"fid";s:3:"327";s:3:"nid";s:3:"675";s:8:"filename";s:14:"mechanisms.pdf";s:8:"filepath";s:40:"files/sch